import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-users-list',
  templateUrl: './users-list.component.html'
})
export class UsersListComponent {
  public users: UserEntity[] = [];

  /*
  constructor(http: HttpClient, @Inject('BACKEND_URL') backendUrl: string) {


    //const req = http.get<UserEntity[]>(backendUrl + 'users');




    http.get<UserEntity[]>(backendUrl + 'users').subscribe(
      result => { this.users = result; },
      error => { console.error(error); });
  }
  */

  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    http.get<UserEntity[]>(baseUrl + 'users').subscribe(
      result => { this.users = result; },
      error => { console.error(error); });
  }
}

interface UserEntity {
  userId: string;
  userName: string;
  createDate: string;
  modifiedDate: string;
}
<h1 id="tableLabel">Users</h1>

<p>Users Listing.</p>

<p *ngIf="!users"><em>Loading...</em></p>

<table class='table table-striped' aria-labelledby="tableLabel" *ngIf="users">
    <thead>
        <tr>
            <th>Name</th>
            <th>Create Date</th>
            <th>Modified Date</th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let user of users">
            <td>{{ user.userName }}</td>
            <td>{{ user.createDate }}</td>
            <td>{{ user.modifiedDate }}</td>
        </tr>
    </tbody>
</table>
